import {
  PROJECT_DIST_DIR,
  PROJECT_SITE_DIST_DIR
} from "./chunk-D3YCLLHO.js";

// src/commands/clean.ts
import { remove } from "fs-extra";
async function cleanProjectDistDir() {
  await remove(PROJECT_DIST_DIR);
}
async function cleanProjectSiteDir() {
  await remove(PROJECT_SITE_DIST_DIR);
}
async function clean() {
  await Promise.all([
    cleanProjectDistDir(),
    cleanProjectSiteDir()
  ]);
}

export {
  cleanProjectDistDir,
  cleanProjectSiteDir,
  clean
};
